function run(inputJson, statusAPI) { 

    const statusRepos = Number(statusAPI); // ou parseInt(statusAPI)

    // Verificar se o status da API √© diferente de 200
    if (statusRepos != 200) {
        // Retorna uma mensagem de erro clara no formato JSON
        return JSON.stringify( statusRepos);
    }


    // Parse do JSON recebido
    const repos = JSON.parse(inputJson);

    // Cria√ß√£o do array de itens no formato desejado
    const items = repos.map(repo => {
        const imageFormat = repo.imageFormat || '1x1'; 

        return {
            "header": {
                "type": "application/vnd.lime.media-link+json",
                "value": {
                    "title": repo.full_name,  // T√≠tulo do item (provido pela API)
                    "text": repo.description, // Subt√≠tulo do item (provido pela API)
                    "type": "image/jpeg",  // Tipo da imagem
                    "uri": repo.owner.avatar_url,  // URI da imagem (provido pela API)
                    "imageFormat": imageFormat  // Formato da imagem (1x1 ou 2x1)
                }
            }
        };
    });

    // Estrutura final no formato esperado
    const response = {
        "itemType": "application/vnd.lime.document-select+json",
        "items": items
    };

    // Retorna o JSON final
    return JSON.stringify(response);
}


